(function ($) {
    $.fn.readmore = function (settings) {
        var opts = $.extend({}, $.fn.readmore.defaults, settings);

        this.each(function () {
            $(this).data('opts', opts);
            if ($(this).html().length > opts.substr_len) {
                abridge($(this));
                linkage($(this));
            }
        });

        function linkage(elem) {
            elem.append(elem.data('opts').more_link);
            elem.children('.more').on('click', function (event) {
                event.preventDefault();

                $(this).hide();
                $(this).siblings('span:not(.hidden-text)').hide().siblings('span.hidden-text').animate({'opacity': 'toggle'}, 1000);
            });
        }

        function abridge(elem) {
            var opts = elem.data('opts');
            var txt = elem.html();
            var len = opts.substr_len;
            var dots = '<span>' + opts.ellipses + '</span>';
            var charAtLen = txt.substr(len, 1);
            while (len < txt.length && !/\s/.test(charAtLen)) {
                len++;
                charAtLen = txt.substr(len, 1);
            }
            var shown = txt.substring(0, len) + dots;
            var hidden = '<span class="hidden-text" style="display: none;">' + txt.substring(len, txt.length) + '</span>';
            elem.html(shown + hidden);
        }

        return this;
    };

    $.fn.readmore.defaults = {
        substr_len: 300,
        ellipses: '&#8230; ',
        more_link: '<a class="more" href="#" style="cursor: pointer; text-decoration: none; border-bottom: #008bd7 dashed 1px;"></a>'
    };

})(jQuery);
